import React from 'react';
import { Redirect } from 'react-router'
import { withRouter } from "react-router-dom";
import { Container, Row, Col, Button, Form} from 'react-bootstrap'
import { useAuth } from "../../Helpers/auth/auth.js";
import './login.css'
class LoginPage extends React.Component { 
    constructor(props, context) {
        super(props, context)
        this.username = React.createRef(); 
        this.password = React.createRef(); 
     }
    login() {
        const {isAuthenticated, setAuthenticated} = useAuth();
        if (this.username == 'test' && this.password == 'test') {
            setAuthenticated(true)
            var path = this.props.location.state.referrer
            if (path == undefined) {
                this.props.history.push('/')
            } else {
                this.props.history.push(path)
            }
        } else {
            setAuthenticated(false)
        }
    }
    render(){
        return(
        <Container>
            <Row>
                <Col xs={12} sm = {{span: 10, offset : 1}}>
                    <Form>
                        <Form.Group controlId="username" >
                            <Form.Row>
                                <Col xs={4} md={2}>
                                    <Form.Label>Username : </Form.Label>
                                </Col>
                                <Col xs={8} md={10}>
                                    <Form.Control ref={this.username} type="text"  placeholder="Username">
                                    </Form.Control>
                                </Col>
                            </Form.Row>
                            <Form.Row>
                                <Col xs={4} md={2}>
                                    <Form.Label>Password : </Form.Label>
                                </Col>
                                <Col xs={8} md={10}>
                                    <Form.Control ref={this.password} type="password" placeholder="Password">
                                    </Form.Control>
                                </Col>
                            </Form.Row>
                        </Form.Group>
                    </Form>
                    <Row>
                        <Col>
                            <Button variant="primary" onclick={this.login} >Log In</Button>
                        </Col>
                    </Row>
                </Col>
            </Row>
        </Container>
        )
    }
}
export default withRouter(LoginPage);
