import React from 'react';
import ReactDOM from 'react-dom';
import { Container, Row, Col, Button } from 'react-bootstrap'
import PDFExporter from '../../Helpers/PDFExporter/PDFExporter.js'
// Props
// title = string
// options= { key : string}
// id = string
// onSelect = (key, event) => {}
class PDFExportForm extends React.Component {
    exporter = new PDFExporter()

    ViewPDF = () => {
        // import pdf converter
        // convert to pdf
        ReactDOM.render(this.exporter.ToPDF(), document.getElementById('pdf'));
    }
    render() { return (
        <Container>
            <Row>
                <Col>{this.props.children}</Col>
            </Row>
            <Row className="buttonRow">
                <Col xs={{span: 4, offset : 8}}>
                    <Button variant="secondary" onClick={this.ViewPDF}>View PDF</Button>
                    <Button variant="primary" style={{marginLeft : '10px',}}>Export PDF</Button>
                </Col>
            </Row>
            <Row>
                <Col>
                    <div id="pdf"></div>
                </Col>
            </Row>
        </Container>
    )}
}

export default PDFExportForm;
