import React from 'react';
import { Alert } from 'react-bootstrap'
class Messages extends React.Component { 
    constructor(props, context) {
        super(props, context)
        this.state = {
            messages : [],
            showBool : []
        }
    }
    static getDerivedStateFromProps(nextProps, prevState) {
        var prevMessages = prevState.messages
        var prevBools = prevState.showBool
        var shouldUpdate = false
        for (var i=0; i < nextProps.messages.length; i++) {
            var msg = nextProps.messages[i]
            if (!prevMessages.includes(msg)) {
                prevMessages.push(msg)
                prevBools.push(true)
                shouldUpdate = true
            }
        }
        if (!shouldUpdate) {
            return null
        } else {
            prevState.messages = prevMessages
            prevState.showBool = prevBools
            return prevState
        }

    }
    renderMessages = () => {
        if (! Array.isArray(this.state.messages)) {
            return
        }
        var messages = []
        for (var i=0; i < this.state.messages.length; i++) {
            var msg = this.state.messages[i]
            var show = this.state.showBool[i]
            if (!show) {
                continue
            }
            var classNames = "alert alert-dismissible fade show alert-" + msg.type
            messages.push(
                <div class={classNames} role="alert">
                    {msg.message}
                    <button type="button" class="close" data-dismiss="alert" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
            )
        }
        return messages 
    }
    render() {
        return (
            <div>
                {this.renderMessages()}
            </div>
        )
    }
}
export default Messages
