import React from 'react';
import { Document, Page, Text, View, StyleSheet } from '@react-pdf/renderer';
import ReactPDF from '@react-pdf/renderer';

const styles = StyleSheet.create({
    page: {
      flexDirection: 'row',
      backgroundColor: '#E4E4E4'
    },
    section: {
      margin: 10,
      padding: 10,
      flexGrow: 1
    }
  });
const TestDocument = () => (
    <Document>
      <Page size="A4" style={styles.page}>
        <View style={styles.section}>
          <Text>Section #1</Text>
        </View>
        <View style={styles.section}>
          <Text>Section #2</Text>
        </View>
      </Page>
    </Document>
  );

class PDFExporter {
    // Create styles
    styles = StyleSheet.create({
        page: {
        flexDirection: 'row',
        backgroundColor: '#E4E4E4'
        },
        section: {
        margin: 10,
        padding: 10,
        flexGrow: 1
        }
    });
   
    // Create Document Component
    MyDocument = () => {
        return (
            <Document>
                <Page size="A4" style={this.styles.page}>
                    <View style={this.styles.section}>
                    <Text>Section #1</Text>
                    </View>
                    <View style={this.styles.section}>
                    <Text>Section #2</Text>
                    </View>
                </Page>
            </Document>
        )
    };

    ToPDF = () => {
        var documentComponent = this.MyDocument();
        console.log(documentComponent)
        ReactPDF.render(<TestDocument />, `${__dirname}/example.pdf`);
    }
}
export default PDFExporter;
