import React from 'react';
import { withRouter } from "react-router-dom";
import { Container, Row, Col, Button, Form} from 'react-bootstrap'
import {AuthContext, authenticate} from '../../Helpers/auth/auth.js'
import { useMsg} from '../../App.js'
import './login.css'
class LoginPage extends React.Component { 
    constructor(props, context) {
        super(props, context)
        this.username = React.createRef(); 
        this.password = React.createRef(); 
        this.state = {
            errors : []
        }
     }
    login = (authManager) => {
        authenticate(this.username.value, this.password.value, authManager,
            (success) => {
                if (success) {
                    var path = this.props.location.state.referrer
                    if (path === undefined) {
                        this.props.history.push('/')
                    } else {
                        this.props.history.push(path)
                    }
                } else {
                    console.log(messageManager)
                    // var oldMessages = msgManager.messages
                    // oldMessages.messages.push({type : "danger", message : "Error : Invalid username and password"})
                    // msgManager.setMessages(oldMessages)
                }
            })
    }
    render(){
        return(
        <AuthContext.Consumer>
            {authManager => (
                <Container>
                    <Row>
                        <Col xs={12} sm = {{span: 10, offset : 1}}>
                            <Form>
                                <Form.Group controlId="username" >
                                    <Form.Row>
                                        <Col xs={4} md={2}>
                                            <Form.Label>Username : </Form.Label>
                                        </Col>
                                        <Col xs={8} md={10}>
                                            <Form.Control ref={(ref) => {this.username = ref}} type="text"  placeholder="Username" autoComplete="username">
                                            </Form.Control>
                                        </Col>
                                    </Form.Row>
                                </Form.Group>
                                <Form.Group controlId="password" >
                                    <Form.Row>
                                        <Col xs={4} md={2}>
                                            <Form.Label>Password : </Form.Label>
                                        </Col>
                                        <Col xs={8} md={10}>
                                            <Form.Control ref={(ref) => {this.password = ref}} type="password" placeholder="Password" autoComplete="current-password">
                                            </Form.Control>
                                        </Col>
                                    </Form.Row>
                                </Form.Group>
                            </Form>
                            <Row>
                                <Col style={{textAlign : 'right'}}>
                                    <Button variant="primary" onClick={() => this.login(authManager)} >Log In</Button>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                </Container>
            )}
        </AuthContext.Consumer>
        )
    }
}
export default withRouter(LoginPage);
