import React from 'react';
import { Alert } from 'react-bootstrap'

function AlertDismissible() {
    const [show, setShow] = useState(true);
  
    if (show) {
      return (
        <Alert variant="danger" onClose={() => setShow(false)} dismissible>
          {this.props.message}
        </Alert>
      );
    }
  }
  export default AlertDismissible;
